def keyRelease(a):
    l =list(a.widget.get())
    l.reverse()
    for i in range(len(l)):
        if not(48 <= ord(l[i]) <= 57 or ord(l[i]) == 46):
            a.widget.delete(len(l)-i-1)
        elif ord(l[i]) == 46:
            for j in range(i+1,len(l)-1):
                if ord(l[j]) == 46:
                    a.widget.delete(len(l)-i-1) 
                    break  #很关键

