on_hit = False
def go():
    global on_hit
    if on_hit == False:
        on_hit = True
        messageWarn.set('')
        try:
            if round(delta(eval(e3.get()),eval(e2.get()),eval(e1.get())),4)!=0.0000:
                birefringence.set('{:.4f}'.format(delta(eval(e3.get()),eval(e2.get()),eval(e1.get()))))
            elif round(delta(eval(e3.get()),eval(e2.get()),eval(e1.get())),8)!=0.0000:
                birefringence.set('{:.8f}'.format(delta(eval(e3.get()),eval(e2.get()),eval(e1.get()))))
            else:
                birefringence.set('{:.0f}'.format(delta(eval(e3.get()),eval(e2.get()),eval(e1.get()))))
        except:
            birefringence.set('输入正确的\n直径及补偿角数值')
            messageWarn.set('注意它们的单位和取值范围\nθ∈[0,60],且两补偿角在30°两边')
